import { Connection, PublicKey} from "@solana/web3.js";
import { type } from "os";

function GetBalance(){
    const connection = new Connection("https://api.testnet.solana.com", "confirmed");
    const myAddress = new PublicKey("GDEfuPi88XF7JiTseS8P1Eiig9MiXAFjeWF9nfWF2T6i");
    
    const programid = new PublicKey("GDEfuPi88XF7JiTseS8P1Eiig9MiXAFjeWF9nfWF2T6i");
    const mint = new PublicKey("4Vyzy4NdXjmRezHTWVsp8HLyL3vBQyxzc7hepS6tXbya");
    
    const data = connection.getTokenAccountsByOwner(myAddress,{mint:mint,programId:programid},'confirmed');
    return(
    <div className="content">
     {JSON.stringify(data)} 
    </div>)
    
}
export default GetBalance;
