import { Connection, PublicKey} from "@solana/web3.js";
import { type } from "os";

async function GetBalance(){
    const connection = new Connection("https://api.testnet.solana.com", "confirmed");
    const myAddress = new PublicKey("GDEfuPi88XF7JiTseS8P1Eiig9MiXAFjeWF9nfWF2T6i");
    
    const programid = new PublicKey("11111111111111111111111111111111");
    const mint = new PublicKey("GqFc4pgQPg7rzznWy36ut5vq1S5rLyfr2VbJe6putUis");
    
    const data = await connection.getTokenAccountBalance(mint,'confirmed');
    return(
    <div className="content">
     {JSON.stringify(data.value)} 
    </div>)
    
}
export default GetBalance;
