import { Connection, PublicKey} from "@solana/web3.js";
import { type } from "os";

function GetBalance(){
    const connection = new Connection("https://api.testnet.solana.com", "confirmed");
    const myAddress = new PublicKey("GDEfuPi88XF7JiTseS8P1Eiig9MiXAFjeWF9nfWF2T6i");
    const myAddress1 = new PublicKey("GDEfuPi88XF7JiTseS8P1Eiig9MiXAFjeWF9nfWF2T6i");
    const myAddress2 = new PublicKey("GqFc4pgQPg7rzznWy36ut5vq1S5rLyfr2VbJe6putUis");
    const data = connection.getTokenAccountsByOwner(myAddress,{mint:myAddress2});
    return(
    <div className="content">
     {JSON.stringify(data)} 
    </div>)
    
}
export default GetBalance;
