import React from 'react';
import './App.css';
import {CheckData, SendData} from "./Firebase";

class Signup extends React.Component {

    private userData: any = {};
    private usernames: Array<string> = ['subham', 'om'];

    async send(username: string, password: string, address: string) {
        const data = await CheckData(username);
        if (data !== "No data available") {
            console.log("Return");
            return;
        }
        await SendData(username, password, address);

    }

    async isDataAvailable(username: string) {
        const data = await CheckData(username);
        if (data !== "No data available") {
            return true
        }
        return false
    }


    state = {
        show: true,
        message: "",
        color: "",
        usernameAvailable : false
    };


    writeUserData = () => {
        console.log("DATA SAVED");
    };

    getUserData = () => {
        console.log("DATA COLLECTED");
    };

    changeUI() {
        this.setState({show: !this.state.show});
    }

    changeMessage(msg: string, color: string) {
        this.setState({message: msg});
        this.setState({color: color});

    }

    onValueChange = (key: string, value: string) => {
        this.userData[key] = value;
    }

    onUpdate(event : any , message : string , color : string , checkAvailability : boolean){
        this.userData["username"] = event.target.value;
        this.state.usernameAvailable = checkAvailability;
        this.changeMessage(message, color)
    }


    render() {
        return (
            <div className="App">
                <header className="App-header">
                    <h1>Solana Wallet</h1>
                    <div>
                        {
                            this.state.show ?
                                <div>
                                    <input id="username"
                                           type="text"
                                           placeholder="Enter username"
                                           onChange={
                                               async (evt) => {

                                                   if (evt.target.value === '') {
                                                       this.onUpdate(evt,"","#000000",false)
                                                       return
                                                   }

                                                   if (await this.isDataAvailable(evt.target.value)) {
                                                       this.onUpdate(evt,"Username is not available","#ff0000",false)
                                                   }
                                                   else {
                                                       this.onUpdate(evt,"Username is available","#00ff00",true)
                                                   }
                                               }
                                           }/>
                                    <br/>
                                    <a style={
                                        {
                                            color: this.state.color
                                        }
                                    }>
                                        {this.state.message}
                                    </a>

                                    <br/><br/>

                                    <input id="password"
                                           type="password"
                                           placeholder="Enter password"
                                           onChange={
                                               (evt) => {
                                                   this.onValueChange("pass", evt.target.value)
                                               }
                                           }
                                    />
                                    <br/><br/>
                                    <button
                                        onClick={
                                            () => {
                                                this.changeUI(); // Address Connect
                                                for (let [key, value] of Object.entries(this.userData)) {
                                                    console.log(key + " --> " + value) // get id and password of the user
                                                }
                                            }
                                        }>
                                        Sign Up
                                    </button>

                                </div> :
                                <div>
                                    <button
                                        onClick={
                                            () => {
                                            }
                                        }
                                        style={
                                            {
                                                marginRight: 30,
                                            }
                                        }>
                                        Connect Wallet
                                    </button>

                                    <button
                                        onClick={
                                            () => {

                                            }
                                        }>
                                        Disconnect Wallet
                                    </button>

                                </div>
                        }
                    </div>
                </header>
            </div>
        );
    }
}

export default Signup;
