// Import the functions you need from the SDKs you need
import { initializeApp } from 'firebase/app';
import "firebase/analytics";
import "firebase/auth";
import { getFirestore, collection, getDocs } from 'firebase/firestore/lite';
import { getDatabase, ref, set,child, get} from "firebase/database";
// TODO: Add SDKs for Firebase products that you want to use
// https://firebase.google.com/docs/web/setup#available-libraries

// Your web app's Firebase configuration
// For Firebase JS SDK v7.20.0 and later, measurementId is optional
const firebaseConfig = {
  apiKey: "AIzaSyAZjuhDPmon1DnzRutD9a9YrlDxllu0mQQ",
  authDomain: "solana-30d78.firebaseapp.com",
  databaseURL: "https://solana-30d78-default-rtdb.firebaseio.com",
  projectId: "solana-30d78",
  storageBucket: "solana-30d78.appspot.com",
  messagingSenderId: "516735150719",
  appId: "1:516735150719:web:457516c160125aab022d4a",
  measurementId: "G-HG5NVS1M1V"
};
const app = initializeApp(firebaseConfig);
const db = getDatabase(app);
const dbRef = ref(db);
export function SendData(UserName:string, Password:string)
{
  console.log("Sending");
  set(ref(db, 'users/' + UserName), {
    username: UserName,
    Password: Password,
  });
}
export function CheckData(UserName:string)
{
  var value;
  console.log("Checking");
  get(child(dbRef, `users/${UserName}`)).then((snapshot) => {
    if (snapshot.exists()) {
      console.log(snapshot.val());
      return String("Data available");
    } else {
      console.log("No data available");
      return String("No data available");
    }
  }).catch((error) => {
    console.error(error);
  });
}
